package com.droven.swipeupdatelayout.view;import android.view.View;import android.widget.TextView;import com.droven.swipeupdatelayout.R;import com.droven.swipeupdatelayout.base.BaseRefreshView;public class SwipeUpdateHeaderView extends BaseRefreshView {    private HeaderRefreshIcon headerIcon;    private TextView headerTv;    private int maxCanRefreshHeight = 0;    @Override    protected int layoutResId() {        return R.layout.vrv_header_view;    }    @Override    protected void initView(View view) {        headerIcon = view.findViewById(R.id.header_refresh_icon);        headerTv = view.findViewById(R.id.header_tv);    }    @Override    public int canRefreshHeight() {        if (maxCanRefreshHeight == 0) {            maxCanRefreshHeight = getViewVerticalSpace(headerIcon) + getViewVerticalSpace(headerTv);        }        return maxCanRefreshHeight;    }    @Override    public void onNormal() {        headerTv.setText("下拉刷新");    }    @Override    public void onCanRefresh() {        headerTv.setText("释放刷新");    }    @Override    public void onRefreshing() {        headerTv.setText("正在刷新");        headerIcon.startAnim();    }    @Override    public void onComplete() {        headerTv.setText("刷新成功");        headerIcon.stopAnim();    }    @Override    public void onDragging(int top) {        if (top >= maxDragHeight())            return;        if (top % 10 == 0) {            headerIcon.invalidate();        }    }}